import React from "react";
import { Modal , Button } from "react-bootstrap";


export default class confirmationPrompt extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      show: false,
      title:'',
      text:'',
    };
  }
  render() {
    return (
      <Modal show={this.state.show} centered>
        <Modal.Header closeButton>
          <Modal.Title>{this.state.tittle}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>{this.state.text}</p>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="Cancelar">Close</Button>
          <Button variant="Confirmar">Save changes</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
